INSERT INTO `ps_xipposts_lang` (`id_xipposts`, `id_lang`, `post_title`, `meta_title`, `meta_description`, `meta_keyword`, `post_content`, `post_excerpt`, `link_rewrite`) VALUES
(2, 1, ' Kilometre Zero Paris', 'Zamounda officiel', 'Crée il y a 6 ans, LE CONCEPT ZAMOUNDA va au delà du style vestimentaire, c\'est un état d\'esprit une philosophie voir un mode de vie basé sur des valeurs et principes communs comme le partage, positivé, l\'humilité, la paix, la famille et l\'amour... Inspiré du dandysme et des gentlemen, les membres du Zamounda mettent un point d\'honneur à faire un lien entre la modernité africaine et occidentale, s\'imposant plus comme des concepteurs que des sapeurs.\r\n\r\nIls revisitent le style des \" Classic men\". Pour les concepteurs du Zamounda ce n\'est pas le prix qui fait l\'élégence mais plutot la façon dont les vetements s\'accordent.', 'zamounda,kilometrezeroparis,zamoundaofficiel,shoes,basket,paris,mode,model,influencer,shooting', '<p>Le collectif Zamounda a eu l\'honneur de collaborer avec la marque Kilometrezero Paris.</p>\n<p>Une marque à l’esprit parisien bien affirmé dont les chaussures sont conçues à Paris, un savoir faire traditionnel qualificatif.</p>\n<p>Rendez-vous sur <a href=\"http://www.kilometrezero.paris\" target=\"_blank\">http://www.kilometrezero.paris</a></p>', 'Collaboration avec la marque Kilometre Zero Paris', '-kilometre-zero-paris'),
(3, 1, 'Dapper Men & Ladies', 'Shooting Dapper Libre', 'Crée il y a 6 ans, LE CONCEPT ZAMOUNDA va au delà du style vestimentaire, c\'est un état d\'esprit une philosophie voir un mode de vie basé sur des valeurs et principes communs comme le partage, positivé, l\'humilité, la paix, la famille et l\'amour... Inspiré du dandysme et des gentlemen, les membres du Zamounda mettent un point d\'honneur à faire un lien entre la modernité africaine et occidentale, s\'imposant plus comme des concepteurs que des sapeurs.\r\n\r\nIls revisitent le style des \" Classic men\". Pour les concepteurs du Zamounda ce n\'est pas le prix qui fait l\'élégence mais plutot la façon dont les vetements s\'accordent.', 'classicmen,zamounda,zamoundaofficiel,men,blog,blogger,paris,mode,model', '', 'Shooting au thème Dapper esprit libre selon la créativité de chaque modèle', 'dapper-men-ladies'),
(4, 1, 'Miss Africa 2018', 'This is Fourth Post For XipBlog', 'Crée il y a 6 ans, LE CONCEPT ZAMOUNDA va au delà du style vestimentaire, c\'est un état d\'esprit une philosophie voir un mode de vie basé sur des valeurs et principes communs comme le partage, positivé, l\'humilité, la paix, la famille et l\'amour... Inspiré du dandysme et des gentlemen, les membres du Zamounda mettent un point d\'honneur à faire un lien entre la modernité africaine et occidentale, s\'imposant plus comme des concepteurs que des sapeurs.\r\n\r\nIls revisitent le style des \" Classic men\". Pour les concepteurs du Zamounda ce n\'est pas le prix qui fait l\'élégence mais plutot la façon dont les vetements s\'accordent.', 'miss,africa,zamounda,zamoundaofficiel', '', 'Invité d\'honneur à l\'événement Miss Africa 2018.', 'miss-africa-2018'),
(8, 1, 'Zamounda x Yves Dorsay', 'Zamounda collaboration yves dorsay', 'Crée il y a 6 ans, LE CONCEPT ZAMOUNDA va au delà du style vestimentaire, c\'est un état d\'esprit une philosophie voir un mode de vie basé sur des valeurs et principes communs comme le partage, positivé, l\'humilité, la paix, la famille et l\'amour... Inspiré du dandysme et des gentlemen, les membres du Zamounda mettent un point d\'honneur à faire un lien entre la modernité africaine et occidentale, s\'imposant plus comme des concepteurs que des sapeurs.\r\n\r\nIls revisitent le style des \" Classic men\". Pour les concepteurs du Zamounda ce n\'est pas le prix qui fait l\'élégence mais plutot la façon dont les vetements s\'accordent.', 'mode,model,zamounda,zamoundaofficiel,shooting,classicmen,blog,blogger,paris', '<p>Crée il y a 6 ans, LE CONCEPT ZAMOUNDA va au delà du style vestimentaire, c\'est un état d\'esprit une philosophie voir un mode de vie basé sur des valeurs et principes communs comme le partage, positivé, l\'humilité, la paix, la famille et l\'amour... Inspiré du dandysme et des gentlemen, les membres du Zamounda mettent un point d\'honneur à faire un lien entre la modernité africaine et occidentale, s\'imposant plus comme des concepteurs que des sapeurs.<br /><br />Ils revisitent le style des \" Classic men\". Pour les concepteurs du Zamounda ce n\'est pas le prix qui fait l\'élégence mais plutot la façon dont les vetements s\'accordent.</p>', 'Collaboration Zamounda x Yves Dorsay', 'zamounda-x-yves-dorsay'),
(9, 1, 'Conférence ICD Paris', 'Zamounda conference', 'Crée il y a 6 ans, LE CONCEPT ZAMOUNDA va au delà du style vestimentaire, c\'est un état d\'esprit une philosophie voir un mode de vie basé sur des valeurs et principes communs comme le partage, positivé, l\'humilité, la paix, la famille et l\'amour... Inspiré du dandysme et des gentlemen, les membres du Zamounda mettent un point d\'honneur à faire un lien entre la modernité africaine et occidentale, s\'imposant plus comme des concepteurs que des sapeurs.\r\n\r\nIls revisitent le style des \" Classic men\". Pour les concepteurs du Zamounda ce n\'est pas le prix qui fait l\'élégence mais plutot la façon dont les vetements s\'accordent.', 'zamounda,zamoundaofficiel,dapper,dandy,fashion,mode,blog,blogger', '<p>Nous avons eu l\'honneur de tenir une conférence organisée par les élèves de la faculté ICD Paris afin d\'exprimer notre vision de la mode. Zamounda, un état d\'esprit.</p>', 'Conférence à la faculté ICD Paris', 'conference-icd-paris'),
(10, 1, 'Casual Chic', 'Zamounda casual chic shooting theme', 'Crée il y a 6 ans, LE CONCEPT ZAMOUNDA va au delà du style vestimentaire, c\'est un état d\'esprit une philosophie voir un mode de vie basé sur des valeurs et principes communs comme le partage, positivé, l\'humilité, la paix, la famille et l\'amour... Inspiré du dandysme et des gentlemen, les membres du Zamounda mettent un point d\'honneur à faire un lien entre la modernité africaine et occidentale, s\'imposant plus comme des concepteurs que des sapeurs.\r\n\r\nIls revisitent le style des \" Classic men\". Pour les concepteurs du Zamounda ce n\'est pas le prix qui fait l\'élégence mais plutot la façon dont les vetements s\'accordent.', 'casual,mode,chic,dandy,dapper,paris,zamounda,zamoundaofficiel', '<p>Casual Chic shooting !!!</p>', 'Casual Chic shooting', 'casual-chic'),
(11, 1, 'Zamounda x Yves Dorsay 2', 'zamoundaofficiel zamounda shooting  dapper paris dandy blog blogger fashion mode', 'Crée il y a 6 ans, LE CONCEPT ZAMOUNDA va au delà du style vestimentaire, c\'est un état d\'esprit une philosophie voir un mode de vie basé sur des valeurs et principes communs comme le partage, positivé, l\'humilité, la paix, la famille et l\'amour... Inspiré du dandysme et des gentlemen, les membres du Zamounda mettent un point d\'honneur à faire un lien entre la modernité africaine et occidentale, s\'imposant plus comme des concepteurs que des sapeurs.\r\n\r\nIls revisitent le style des \" Classic men\". Pour les concepteurs du Zamounda ce n\'est pas le prix qui fait l\'élégence mais plutot la façon dont les vetements s\'accordent.', 'zamoundaofficiel ,zamounda,shooting,dapper,paris,dandy,blog,fashion,model,blogger', '<p>Zamounda x Yves Dorsay 2</p>', 'Zamounda x Yves Dorsay 2', 'zamounda-x-yves-dorsay-2'),
(12, 1, 'PEAKY BLINDERS THEME', 'zamounda PEAKY BLINDERS THEME', 'Inspiré de la série Peaky Blinders,  dont les acteurs en dehors de leur frasque sont surtout connu pour être des hommes aux look atypique. Nous avons décidé de mettre en place un shooting qui revisite le style Dandy et principalement celui des acteurs de la série.\r\n\r\n\r\nLe port du beret est aussi une marque des fabrique typique Peaky Blinders.', 'zamounda,zamoundaofficiel,peaky,blinders,peaky blinders,dandy,london,paris,shooting,model,classicmen,dappermen,mode,model,photo,art,zamounda officiel', '<p>Inspiré de la série Peaky Blinders,  dont les acteurs en dehors de leur frasque sont surtout connu pour être des hommes aux look atypique. Nous avons décidé de mettre en place un shooting qui revisite le style Dandy et principalement celui des acteurs de la série.</p>\n<p></p>\n<p>Le port du beret est aussi une marque des fabrique typique Peaky Blinders.</p>', 'Inspiré de la série Peaky Blinders,  dont les acteurs...', 'peaky-blinders-theme');
